mapper.select:
SELECT id, username, password, email, avatar, nickname, signature, role, created_at, updated_at
FROM user
WHERE username = #{username}

SELECT id, username, password, email, avatar, nickname, signature, role, created_at, updated_at
FROM user
WHERE email = #{email}

SELECT id, username, password, email, avatar, nickname, signature, role, created_at, updated_at
FROM user
WHERE id = #{id}

mapper.update:
UPDATE user
SET nickname = #{nickname}, signature = #{signature}, updated_at = #{updatedAt}
WHERE id = #{id}

UPDATE user
SET avatar = #{avatar}, updated_at = #{updatedAt}
WHERE id = #{id}

UPDATE user
SET password = #{password}, updated_at = #{updatedAt}
WHERE id = #{id}
